import { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Button, Box, Snackbar, Alert } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

type ProfileType = {
  id: number;
  name: string;
  email: string;
  age?: number;
};

const ProfileCard = () => {
  const [profile, setProfile] = useState<ProfileType | null>(null);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/profile`)
      .then((res) => {
        if (Array.isArray(res.data) && res.data.length > 0) {
          setProfile(res.data[res.data.length - 1]); // shows the most recently added
        } else {
          setProfile(null);
        }
      })
      .catch(() => {
        setProfile(null);
      });
  }, []);

  const handleDelete = async () => {
    if (profile?.id) {
      try {
        await axios.delete(`${process.env.REACT_APP_API_URL}/profile/${profile.id}`);
        setSnackbar({ open: true, message: 'Profile deleted.', severity: 'success' });
        setTimeout(() => navigate('/'), 1500);
      } catch (error) {
        setSnackbar({ open: true, message: 'Delete failed.', severity: 'error' });
      }
    }
  };

  return (
    <Box sx={{ maxWidth: 400, mx: 'auto', mt: 5 }}>
      {profile ? (
        <Card>
          <CardContent>
            <Typography variant="h6">Name: {profile.name}</Typography>
            <Typography>Email: {profile.email}</Typography>
            <Typography>Age: {profile.age}</Typography>
            <Button variant="outlined" sx={{ mt: 2, mr: 2 }} onClick={() => navigate('/')}>Edit</Button>
            <Button variant="contained" color="error" sx={{ mt: 2 }} onClick={handleDelete}>Delete</Button>
          </CardContent>
        </Card>
      ) : (
        <Typography variant="body1">No profile found. Please create one.</Typography>
      )}

      <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
        <Alert severity={snackbar.severity as 'success' | 'error'} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default ProfileCard;
