import { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Button, Box, Snackbar, Alert, Paper } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

type ProfileType = {
  id: number;
  name: string;
  email: string;
  age?: number;
};

const ProfileCard = () => {
  const [profile, setProfile] = useState<ProfileType | null>(null);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const navigate = useNavigate();

 useEffect(() => {
  const localProfile = localStorage.getItem('profile');

  if (localProfile) {
    setProfile(JSON.parse(localProfile)); // ✅ Use local data first
  } else {
    axios.get(`${process.env.REACT_APP_API_URL}/profile`)
      .then((res) => {
        if (Array.isArray(res.data) && res.data.length > 0) {
          const latest = res.data[res.data.length - 1];
          setProfile(latest);
          localStorage.setItem('profile', JSON.stringify(latest)); // ✅ Sync localStorage
        } else {
          setProfile(null);
        }
      })
      .catch(() => setProfile(null));
  }
}, []);


  const handleDelete = async () => {
  if (profile?.id) {
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/profile/${profile.id}`);
      setSnackbar({ open: true, message: 'Profile deleted.', severity: 'success' });
      setProfile(null);
    } catch (error) {
      setSnackbar({ open: true, message: 'Delete failed.', severity: 'error' });
    }
  }
};

 return (
    <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', bgcolor: '#f5f7fa' }}>
      <Paper elevation={4} sx={{ p: 4, width: '100%', maxWidth: 420, borderRadius: 3 }}>
        {profile ? (
  <>
    <Typography variant="h5" gutterBottom align="center">Profile Details</Typography>
    <Typography variant="body1" sx={{ mb: 1 }}><strong>Name:</strong> {profile.name}</Typography>
    <Typography variant="body1" sx={{ mb: 1 }}><strong>Email:</strong> {profile.email}</Typography>
    {profile.age !== undefined && (
  <Typography variant="body1" sx={{ mb: 2 }}>
    <strong>Age:</strong> {profile.age}
  </Typography>
)}

    <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
      <Button variant="outlined" onClick={() => navigate('/')}>Edit</Button>
      <Button variant="contained" color="error" onClick={handleDelete}>Delete</Button>
    </Box>
  </>
) : (
  <Box sx={{ textAlign: 'center' }}>
    <Typography variant="body1" sx={{ mb: 2 }}>
      No profile found. Please create one.
    </Typography>
    <Button variant="contained" onClick={() => navigate('/')}>
      Create Profile
    </Button>
  </Box>
)}

        <Snackbar open={snackbar.open} autoHideDuration={3000} onClose={() => setSnackbar({ ...snackbar, open: false })}>
          <Alert severity={snackbar.severity as 'success' | 'error'} sx={{ width: '100%' }}>
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Paper>
    </Box>
  );
};

export default ProfileCard;
